@import "./normalize.scss";
// @import "./components/index.scss";

:root {
  --clr-1: #3f51b5;
  --clr-2: #e86a92;
  --clr-3: #f7e733;
  --clr-4: #f7f7f9;
  --clr-5: #41e2ba;
  --clr-6: #c1666b;

  --dark80: rgba(48, 79, 89, 0.8);
  --dark60: rgba(48, 79, 89, 0.6);
  --transition: all 0.3s ease 0s;

  --ff: "Ubuntu", sans-serif;
  --fw-reg: 400;
  --fl--height: 1.75;

  --fs-h1: 3.052rem;
  --fs-h2: 2.441rem;
  --fs-h3: 1.953rem;
  --fs-h4: 1.563rem;
  --fs-h5: 1.2rem;
  --fs-body: 1.25rem;
  --fs-small: 0.8rem;
}

@media (min-with: 100px) {
}

/* Reset class*/
*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html {
  font-size: 62.5%; /*10px*/
}

html,
body {
  width: 100%;
  min-height: 100%;
}

body {
  // background: linear-gradient(var(--dark60), var(--dark80)),
  //   url("https://source.unsplash.com/daily");
  // background-position: center;
  // background-repeat: no-repeat;
  // background-size: cover;
  font-family: var(--ff);

  font-weight: var(--fw-reg);
  line-height: 1.75;
  overflow: overlay;
  width: 100%;
  // background-color: black;
  // background: black;
}

/* TYPOGRAPHY */

h1,
h2,
h3,
h4,
h5 {
  margin: 3rem 0 1.38rem;
  font-weight: var(--fw-reg);
  line-height: 1.3;
  color: var(--clr-4);
}

h1 {
  // font-size: var(--fs-h1);
  font-size: clamp(3.052rem, -0.875rem + 8.333vw, 5.653rem);
}

h2 {
  font-size: clamp(var(--fs-h2), -0.875rem + 8.333vw, 3.998rem);
  font-size: var(--fs-h2);
}

h3 {
  font-size: var(--fs-h3);
  font-size: clamp(var(--fs-h3), -0.875rem + 8.333vw, 2.827rem);
}

h4 {
  font-size: var(--fs-h4);
  // font-size: clamp(var(--fs-h4), -0.875rem + 8.333vw, 1.999rem);
}

h5 {
  font-size: var(--fs-h5);
  font-size: clamp(var(--fs-h5), -0.875rem + 8.333vw, 1.414rem);
}

p {
  font-size: 1rem;
  margin-bottom: 1rem;
}

small,
.text_small {
  font-size: var(--fs-small);
}
/* TYPOGRAPHY */

/* GLOBAL STYLE */
a {
  text-decoration: none;
  color: inherit;
}

ul {
  list-style-type: none;
}
img {
  display: block;
  height: 100%;
  width: 100%;
  object-fit: contain;
}

.container {
  max-width: 1920;
  margin: 0 auto;
}

main {
  width: 100vw;
}
// Header
header {
  background-color: var(--clr-1);
  height: 8vh;
  .wrapper {
    padding: 0.5rem 3rem;
    position: relative; // Help to center logo
    height: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;

    .logo {
      height: 80%;
      img {
        height: 100%;
        width: auto;
      }
    }
  }
  .nav {
    font-size: 1.25rem;
    color: var(--clr-4);

    &__list {
      display: none;
    }
    @media (min-width: 768px) {
      &__list {
        display: flex;
        display: flex;
        gap: 3rem;
      }
    }
  }
}

// HERO
.hero {
  background-image: url("../assets/img/Hero.png");

  border: solid 1px var(--clr-2);
  // min-height: 50vh;
  @media screen and (min-with: 475px) {
    // min-height: 100vh;
  }
  .wrapper {
    padding: 0.5rem 3rem;
    position: relative;
    height: 100%;
  }
  &__body {
    h1 {
      font-size: var(--fs-h1);
      font-weight: bold;
    }
    h4 {
      font-size: var(--fs-h4);
    }
    display: flex;
    flex-direction: column;
    justify-content: center;
    color: var(--clr-4);
    align-items: center;
    height: 100%;
  }
}
// ***************************     HERO   ******************************** //
@media (min-width: 768px) {
  // display: none;
  .hamburger {
    display: none;
  }
}
// ***************************   SERVICES   ******************************* //

.service {
  margin-bottom: 50px;
  .wrapper {
    display: flex;
    flex-direction: column;
    justify-content: center;
  }
  &__title {
    h2,
    h3 {
      color: black;
      text-align: center;
    }
  }
  .services {
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 1rem;
  }
  .card {
    width: 31.5rem;
    border: solid 1px;
    border-radius: 15px;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    padding: 2rem 2rem;
    background: #0b3d91;
    &__figure {
      width: 32px;
      height: 32px;
      margin: 0 auto;
      &__title {
        color: blue;
      }
    }
  }
}

/* *********** PROPRIETAIRES  ******************** */

.proprietaires {
  width: 100%;

  .wrapper {
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    margin: 0 auto;
  }
  &__header {
    width: 50%;
    margin: 0 auto;
  }
  &__body {
    text-align: center;
    color: #3f3d56;

    h2 {
      font-weight: bold;
    }
  }
  &__footer {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-top: 20px;

    button {
      border-radius: 20px;
      font-weight: bold;
      margin: 5px;
    }

    .button-primary {
      background-color: #0b3d91;
      color: white;
      width: 200px;
      height: 46px;
    }

    .button-secondary {
      border-color: #0b3d91;
      color: #0b3d91;
      width: 200px;
      height: 46px;
    }
  }
}

/* *********** LOCATAIRES  ******************** */

.locataires {
  width: 100%;

  .wrapper {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    margin: 70px 0;
  }
  &__header {
    width: 50%;
    margin: 0 auto;
    color: #3f3d56;
  }
  &__footer {
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin-top: 20px;

    button {
      border-radius: 20px;
      font-weight: bold;
      margin: 5px;
    }

    .button-primary {
      background-color: #0b3d91;
      color: white;
      width: 200px;
      height: 46px;
    }

    .button-secondary {
      border-color: #0b3d91;
      color: #0b3d91;
      width: 200px;
      height: 46px;
    }
  }
}

/* *********** DECOLLAGE  ******************** */

.decollage {
  width: 100%;

  .wrapper {
    display: flex;

    justify-content: center;
    align-items: center;
  }

  &__header {
    width: 50%;
    text-align: center;
    margin: 0 auto;
  }

  &__body {
    width: 50%;
  }

  &__list {
    display: flex;
    justify-content: space-between;
    flex-direction: column;
    align-items: center;
    padding: 15px;
  }

  &__items {
    border: 1px solid #0b3d91;
    background-color: #0b3d91;
    border-radius: 20px;
    color: white;
    padding: 10px;
  }
}

